import csv
import os
from os import path

import requests

baseDir = 'D:\\PycharmProjects\\TFD_Exam\\data'
fname1 = path.join(baseDir, 'csv_files', 'bbc-text.csv')
if not path.exists(fname1):
    url_bbc = 'https://storage.googleapis.com/laurencemoroney-blog.appspot.com/bbc-text.csv'
    req = requests.get(url_bbc)
    with open(fname1, 'wb') as file:
        file.write(req.content)
else:
    print('file {0} exists already'.format(fname1.split(os.sep)[-1]))

# %%
fname2 = path.join(baseDir, 'stopwards.js')
if not path.exists(fname2):
    url_stpw = 'https://github.com/Yoast/YoastSEO.js/blob/develop/src/config/stopwords.js'
    req = requests.get(url_stpw)
    with open(fname2, 'wb') as file:
        file.write(req.content)
else:
    print('file {0} exists already'.format(fname2.split(os.sep)[-1]))
# %%

stopwords = ["a", "about", "above", "after", "again", "against", "all", "am", "an", "and", "any", "are", "as", "at",
             "be", "because", "been", "before", "being", "below", "between", "both", "but", "by", "could", "did", "do",
             "does", "doing", "down", "during", "each", "few", "for", "from", "further", "had", "has", "have", "having",
             "he", "he'd", "he'll", "he's", "her", "here", "here's", "hers", "herself", "him", "himself", "his", "how",
             "how's", "i", "i'd", "i'll", "i'm", "i've", "if", "in", "into", "is", "it", "it's", "its", "itself",
             "let's", "me", "more", "most", "my", "myself", "nor", "of", "on", "once", "only", "or", "other", "ought",
             "our", "ours", "ourselves", "out", "over", "own", "same", "she", "she'd", "she'll", "she's", "should",
             "so", "some", "such", "than", "that", "that's", "the", "their", "theirs", "them", "themselves", "then",
             "there", "there's", "these", "they", "they'd", "they'll", "they're", "they've", "this", "those", "through",
             "to", "too", "under", "until", "up", "very", "was", "we", "we'd", "we'll", "we're", "we've", "were",
             "what", "what's", "when", "when's", "where", "where's", "which", "while", "who", "who's", "whom", "why",
             "why's", "with", "would", "you", "you'd", "you'll", "you're", "you've", "your", "yours", "yourself",
             "yourselves"]

sentences = []
labels = []
words_count = 0
with open(fname1, 'r') as csvfile:
    reader = csv.reader(csvfile, delimiter=',')
    next(reader)
    for row in reader:
        labels.append(row[0])
        sentence = row[1]
        # remove stopwords from sentences
        for word in stopwords:
            token = " " + word + " "
            sentence = sentence.replace(token, " ")
        sentences.append(sentence)

    print(len(sentences))
    print(sentences[0])
    print(words_count)
# %%
from tensorflow.keras.preprocessing.text import Tokenizer
from tensorflow.keras.preprocessing.sequence import pad_sequences

tokenizer = Tokenizer(num_words=int(words_count * 0.05), oov_token='<oov>')
tokenizer.fit_on_texts(sentences)
word_index = tokenizer.word_index
print(len(word_index))
# %%
sequences = tokenizer.texts_to_sequences(sentences)
padded = pad_sequences(sequences=sequences, padding='post')
print(padded[0])
print(padded.shape)
# Expected output
# [  96  176 1158 ...    0    0    0]
# (2225, 2442)
# %%
lebel_tokenizer = Tokenizer(num_words=100, oov_token='<oov>')
lebel_tokenizer.fit_on_texts(labels)
label_seq = lebel_tokenizer.texts_to_sequences(labels)
label_word_index = lebel_tokenizer.word_index
print(label_seq)
print(label_word_index)

